mapper.insert:
INSERT INTO 
	gztls_device( 
		device_id
		, device_name
		, device_ip
		, device_address
		, device_latitude
		, device_longitude
) VALUES( 
		#{device_id}
		, #{device_name}
		, #{device_ip}
		, #{device_address}
		, #{device_latitude}
		, #{device_longitude}
)

mapper.select:
SELECT * FROM gztls_device
AND device_id = #{device_id}
AND device_name LIKE CONCAT('%', #{device_name}, '%')

SELECT a.device_name , a.device_id , b.data_type , b.average FROM gztls_device a JOIN ( SELECT device_id , data_type , AVG(data_content) AS average FROM gztls_success
device_id = #{device_id}
AND server_time BETWEEN #{server_time} AND #{to_date}
AND data_type = #{data_type}
GROUP BY device_id, data_type )b WHERE a.device_id = b.device_id;
