mapper.select:
SELECT a.device_id , a.device_name , a.device_ip , b.err_code , b.err_type , b.err_message , b.server_time FROM gztls_device a JOIN ( SELECT device_ip , err_code , err_type , err_message , server_time FROM gztls_error
server_time = #{server_time}
) b ON a.device_ip = b.device_ip;

mapper.insert:
INSERT INTO 
	gztls_error( 
		device_ip
		, err_code
		, err_type
		, err_message
		, server_time
) VALUES( 
		#{device_ip}
		, #{err_code}
		, #{err_type}
		, #{err_message}
		, #{server_time}
)

mapper.select:
SELECT err_type , COUNT(err_type) as count FROM gztls_error
device_ip = #{device_ip}
GROUP BY err_type;

SELECT b.device_id , b.device_name , a.device_ip , a.month , a.count FROM ( SELECT device_ip, SUBSTR(server_time,6,2) AS month, COUNT(SUBSTR(server_time,6,2)) AS count FROM gztls_error WHERE SUBSTR(server_time,1,4) = #{year} GROUP BY device_ip, SUBSTR(server_time,6,2) ) a LEFT JOIN gztls_device b ON a.device_ip = b.device_ip
device_id = '00000000522291dbb827eb'
ORDER BY month;
